			if(x[i] < 0)x[i] += mod;
			out.println(x[i]);
		}
		// -11 3 5 3
		// -12 4 4 4
		// -3 1 1 1

		// (P0-1)E0 + ... + P(n-1)E(n-1) = -1+?
		// (P1)E0 + ... + P(n-1^1)E(n-1) = -1

		// E0=0
		// (1/6)(-5E0+2E1+E2+2E3) = 3
		// (1/6)(2E0-5E1+2E2+E3) = -1
		// (1/6)(E0+2E1-5E2+2E3) = -1
		// (1/6)(2E0+E1+2E2-5E3) = -1
	}

	static long[] tol(int[] a)
	{
		long[] b = new long[a.length];
		for(int i = 0;i < a.length;i++)b[i] = a[i];
		return b;
	}

	public static long[] fwht(long[] a, int mod)
	{
		int n = a.length;
		assert (n&n-1) == 0;
		a = Arrays.copyOf(a, n);
		for(int w = Integer.numberOfTrailingZeros(n)-1;w >= 0;w--){
			for(int i = 0;i < n;i++){
				if(i<<~w>=0){
					long nal = a[i]+a[i|1<<w];
					long nar = a[i]-a[i|1<<w];
					if(nal >= mod)nal -= mod;
					if(nar < 0)nar += mod;
					a[i] = nal; a[i|1<<w] = nar;
				}
			}
		}
		return a;
	}


	public static long invl(long a, long mod) {
		long b = mod;
		long p = 1, q = 0;
		while (b > 0) {
			long c = a / b;
			long d;
			d = a;
			a = b;
			b = d % b;
			d = p;
			p = q;
			q = d - c * q;
		}
		return p < 0 ? p + mod : p;
	}


