void hadamard(long[] z) {
  foreach (e; 0 .. N) {
    foreach (i; 0 .. 1 << N) {
      const j = i ^ (1 << e);
      if (i < j) {
        const t = (z[i] + z[j]) % MO;
        z[j] = (z[i] - z[j]) % MO;
        z[i] = t;
      }
    }
  }
}

