const int MX=2000;

bool A[MX][MX]={};
bool dp[MX][MX]={};

int main(){
    int n;
    cin>>n;
    char c;
    for(int i=0;i<n;i++){
        dp[i][i]=true;
        for(int j=0;j<i;j++){
            scanf(" %c",&c);
            if(c=='1'){A[i][j]=true;}
            A[j][i]=!A[i][j];
        }
    }
    for(int i=1;i<n;i++){
        for(int j=0;i+j<n;j++){
            int R=i+j;
            int l=j+1;
            bool j1=false;
            for(int k=j+1;k<=R && !j1;k++){
                j1|=A[j][k] & dp[l][k] & dp[R][k];
            }
            dp[R][j]=j1;
        }
        for(int j=i;j<n;j++){
            int R=j-i;
            int l=j-1;
            bool j1=false;
            for(int k=R;k<j && !j1;k++){
                j1|=A[j][k] & dp[l][k] & dp[R][k];
            }
            dp[R][j]=j1;
        }
    }
    int ans=0;
    for(int i=0;i<n;i++){
        if(dp[0][i] && dp[n-1][i]){ans++;}
    }
    cout<<ans<<endl;



    return 0;
}

#include <bits/stdc++.h>

using namespace std;

char ch[2010][2010];

int n;

bitset <2010> B[2010], C[2010];

bitset <2010> Ans[2010];

int main() {
	scanf("%d", &n);
	for(int i = 2; i <= n; i++) scanf("%s", ch[i] + 1);
	for(int i = 2; i <= n; i++)
		for(int j = 1; j < i; j++)
			(B[i][j] = ch[i][j] - '0'), B[j][i] = !B[i][j];
	for(int i = 1; i <= n; i++) Ans[i][i] = 1;
	for(int i = 1; i < n; i++) {
		C[i][i + 1] = B[i][i + 1];
		C[i + 1][i] = B[i + 1][i];
		if(B[i][i + 1]) Ans[i + 1][i] = 1; else Ans[i][i + 1] = 1;
	}
	for(int i = 2; i <= n; i++)
		for(int j = 1; i + j <= n; j++) {
			int l = j, r = i + j;
			C[l][r] = B[l][r];
			C[r][l] = B[r][l];
			bitset <2010> tmp = C[l] & Ans[l + 1] & Ans[r];
			Ans[r][l] = !!tmp.count();
			tmp = C[r] & Ans[l] & Ans[r - 1];
			Ans[l][r] = !!tmp.count();
		}
	int tot = 0;
	for(int i = 1; i <= n; i++) tot += Ans[1][i] && Ans[n][i];
	printf("%d\n", tot);
	return 0;
}

