  }

  for(int i = N - 1; i >= 0; i--) {
    winL[i][i] = winR[i][i] = 1;
    for(int j = i + 1; j < N; j++) {
      sumP[i][j] = 0;
      for(int p = i; p < j; p++) {
        sumP[i][j] += winL[i][p] * winR[p + 1][j];
      }
      winL[i][j] = winR[i][j] = 0;
      {  // winL
        for(int k = i + 1; k <= j; k++) {
          double prob = mat[i][k];
          winL[i][j] += prob * winL[k][j] * sumP[i][k];
        }
      }
      {  // winR
        for(int k = i; k < j; k++) {
          double prob = mat[j][k];
          winR[i][j] += prob * winR[i][k] * sumP[k][j];
        }
      }
      winL[i][j] /= (j - i);
      winR[i][j] /= (j - i);
    }
  }
  int ret = 0;
  for(int i = 0; i < N; i++) {
    double ans = winR[0][i] * winL[i][N - 1];
    ret += ans > 0;
  }
  cout << ret << endl;
}

