using namespace std;

#define fastInp cin.tie(0); cout.tie(0); ios_base::sync_with_stdio(0);

typedef long long ll;
typedef long double ld;

const ll SZ = 2010;

ll n;
bool grid[SZ][SZ];
int sv[SZ][SZ], st[SZ][SZ];
vector<ll> wn[SZ];

bool find(int l, int r) {
	if (l == r - 1) return 1;
	if (sv[l][r] != -1) return sv[l][r];

	int ind;
	ind = st[l][l];
	while (ind < wn[l].size() && wn[l][ind] < r) {
		int i = wn[l][ind];
		bool fl1 = find(l, i);
		if (fl1) {
			if (find(i, r)) {
				sv[l][r] = 1;
				return 1;
			}
		}
		ind++;
	}

	ind = st[r][l];
	while (ind < wn[r].size() && wn[r][ind] < r) {
		int i = wn[r][ind];
		bool fl1 = find(l, i);
		if (fl1) {
			if (find(i, r)) {
				sv[l][r] = 1;
				return 1;
			}
		}
		ind++;
	}

	sv[l][r] = 0;
	return 0;
}

int main()
{
	fastInp;
	int start = clock();

	cin >> n;

	for (int i = 0; i < n + 3; i++) {
		for (int j = 0; j < n + 3; j++) {
			sv[i][j] = -1;
		}
	}
	for (int i = 1; i < n; i++) {
		string s;
		cin >> s;
		for (int j = 0; j < s.size(); j++) {
			if (s[j] == '1') {
				grid[i][j] = 1;
				wn[i + 1].push_back(j + 1);
			}
			else {
				grid[j][i] = 1;
				wn[j + 1].push_back(i + 1);
			}
		}
	}

	for (int i = 1; i <= n; i++) {
		sort(wn[i].begin(), wn[i].end());
	}

	for (int i = 1; i <= n; i++) {
		int curInd = wn[i].size();
		for (int j = n + 1; j >= 0; j--) {
			if (curInd != 0 && wn[i][curInd - 1] > j) curInd--;
			st[i][j] = curInd;
		}
	}

	ll ans = 0;

	vector<ll> perm;
	for (int i = 1; i <= n; i++) perm.push_back(i);

	random_shuffle(perm.begin(), perm.end());
	for (int i2 = 1; i2 <= n; i2++) {
		int end = clock();
		int t = (end - start);
		if (t >= 100000) break;
		int i = perm[i2];
		bool fl1 = find(0, i);
		if (fl1) {
			if (find(i, n + 1)) {
				ans++;
			}
		}
	}

	cout << ans;

	return 0;
}

