void solve() {
	int n; cin >> n;
	rep1(i, n - 1) {
		rep(j, i) {
			char t; cin >> t;
			if (t == '1') {
				b[i][j] = true;
			}
			else {
				b[j][i] = true;
			}
		}
	}
	rep(i, n)le[i][i] = ri[i][i] = true;

	int ans = 0;
	Rep1(i, 2, n) {
		rep(j, n - i + 1) {
			int l = j;
			int r = j + i - 1;
			bool fl = false, fr = false;
			Rep1(k, l + 1, r) {
				fl |= b[l][k] & ri[l + 1][k] & le[r][k];
			}
			Rep(k, l, r) {
				fr |= b[r][k] & ri[l][k] & le[r - 1][k];
			}
			le[r][l] = fl, ri[l][r] = fr;
		}
	}
	rep(i, n) {
		if (ri[0][i] && le[n - 1][i])ans++;
	}
	cout << ans << endl;
}

