template <size_t N> bool bitsetOpAny(bitset<N> &a, bitset<N> &b, bitset<N> &c, bitset<N> &d) {
  constexpr size_t nw = (N + 64 - 1) / 64;
  auto a_ = (uint64_t *)&a;
  auto b_ = (uint64_t *)&b;
  auto c_ = (uint64_t *)&c;
  auto d_ = (uint64_t *)&d;
  for (size_t i = 0; i < nw; ++i) {
    if ((a_[i] | b_[i]) & c_[i] & d_[i]) {
      return true;
    }
  }
  return false;
}


