const int MAXN = (1 << 11);

int n;
bitset<MAXN> A[MAXN];

void read()
{
	cin >> n;
	for(int i = 2; i <= n; i++)
	{
		string s;
		cin >> s;
		for(int j = 1; j < i; j++)
		{
			int x = s[j - 1] - '0';
			A[j][i] = x ^ 1;
			A[i][j] = x;
		}
	}
