bitset<MAXN> lft[MAXN];
bitset<MAXN> rgh[MAXN];
bitset<MAXN> winners[MAXN], emp;

void solve()
{
	for(int i = 1; i <= n; i++)
		lft[i][i] = 1, rgh[i][i] = 1, winners[i][i] = 1;

	for(int len = 2; len <= n; len++)
	{
		for(int i = 1; i <= n; i++)
		{
			int r = i + len - 1;
			if(r > n) continue;
			if((A[i] & winners[i + 1]).count()) lft[r][i] = 1;
		}

		for(int i = 1; i <= n; i++)
		{
			int l = i - len + 1;
			if(l <= 0) continue;
			if((A[i] & winners[l]).count()) rgh[l][i] = 1;
		}

		for(int l = 1; l <= n; l++)
		{
			int r = l + len - 1;
			if(r > n) continue;
			winners[l] = rgh[l] & lft[r];
		}
	}

	cout << winners[1].count() << endl;
}

