#include <bits/stdc++.h>
using namespace std;

const int MAXN = 2e3 + 5;

int N;
bitset <MAXN> beats[MAXN];
bitset <MAXN> lft[MAXN], rig[MAXN];

void load() {
	scanf("%d", &N);
	for (int i = 2; i <= N; i++)
		for (int j = 1; j < i; j++) {
			char x;
			scanf(" %c", &x);
			if (x == '1')
				beats[i].set(j);
			else
				beats[j].set(i);
		}
}

int solve() {
	for (int i = 1; i <= N; i++) {
		lft[i].set(i);
		rig[i].set(i);
	}

	for (int i = 2; i <= N; i++) {
		vector <bool> can_right(N + 1, false), can_left(N + 1, false);
		for (int j = 1; j + i - 1 <= N; j++) {
			can_right[j] = (lft[j + 1] & rig[j + i - 1] & beats[j]).any();
			can_left[j + i - 1] = (lft[j] & rig[j + i - 2] & beats[j + i - 1]).any(); 
		}
		for (int j = 1; j <= N; j++) {
			if (can_right[j])
				rig[j + i - 1].set(j);
			if (can_left[j])
				lft[j - i + 1].set(j);
		}
	}

	int sol = 0;
	for (int i = 1; i <= N; i++)
		sol += lft[1].test(i) && rig[N].test(i);

	return sol;
}

int main() {
	load();
	printf("%d\n", solve());
	return 0;
}

