const int Nmax=2010;

bitset<Nmax> l[Nmax],r[Nmax],l2[Nmax],r2[Nmax];
bool win[Nmax][Nmax];

signed main(){
	int n=read();
	FOR(i,1,n){
		string s=readString();
		rep(j,i){
			win[i][j]=s[j]-'0';
			win[j][i]=!win[i][j];
		}
	}
	rep(i,n+1)
		l[i][i]=1;
	rep(w,n+1){
		rep(i,n-w+1){
			bool x=l[i][i+w]||r[i+w][i];
			r[i+w][i]=x;
			l[i][i+w]=x;
		}
		FOR(i,1,n-w)if(l[i][i+w]){
			if(!win[i-1][i+w]){
				l2[i-1][i+w]=1;
				r[i+w]|=r[i-1];
			}else{
				r2[i+w+1][i]=1;
				l[i]|=l[i+w+1];
			}
		}
		rep(i,n-w+1)if(l[i][i+w]){
			l[i]|=l2[i+w];
			r[i+w]|=r2[i];
		}
	}
	int ans=0;
	rep(i,n)
		if(l[0][i]&&l[i+1][n])
			ans++;
	print(ans);
}

