const int MAXN=2000;

int n;
bool win[MAXN][MAXN];

bool canfst[MAXN][MAXN];
bool canlst[MAXN][MAXN];
bool canfinal[MAXN][MAXN];

bitset<MAXN> bwin[MAXN]; // win[i][k]
bitset<MAXN> bfst[MAXN]; // canfst[k][i]
bitset<MAXN> blst[MAXN]; // canlst[i][k]
bitset<MAXN> bfst2[MAXN]; // canfst[i][k]
bitset<MAXN> blst2[MAXN]; // canlst[k+1][j]
bitset<MAXN> bfin1[MAXN]; // canfinal[i][k]
bitset<MAXN> bfin2[MAXN]; // canfinal[k][i]

int solve() {
	//REP(i,n) { printf("win%d: ",i); REP(j,n) printf("%c",win[i][j]?'v':'.'); puts(""); }
	REP(i,n) REP(j,n) canfst[i][j]=canlst[i][j]=canfinal[i][j]=false;
	REP(i,n) canfst[i][i]=canlst[i][i]=true;
	REP(i,n) bwin[i].reset(),bfst[i].reset(),blst[i].reset(),bfin1[i].reset(),bfin2[i].reset(),bfst2[i].reset(),blst2[i].reset();
	REP(i,n) REP(j,n) if(win[i][j]) bwin[i][j]=true;
	REP(i,n) bfst[i][i]=blst[i][i]=bfst2[i][i]=blst2[i][i]=true;
	FORE(len,2,n) REPE(i,n-len) {
		int j=i+len-1;

		if((bfst2[i]&(blst2[j]>>1)).any()) canfinal[i][j]=true;
		if(canfinal[i][j]) bfin1[i][j]=bfin2[j][i]=true;
		//bool chk1=false; FORE(k,i,j-1) if(canfst[i][k]&&canlst[k+1][j]) chk1=true; assert(chk1==canfinal[i][j]);

		//FORE(k,i+1,j) if(win[i][k]&&canfst[k][j]&&canfinal[i][k]) canfst[i][j]=true;
		if((bwin[i]&bfst[j]&bfin1[i]).any()) canfst[i][j]=true;
		if(canfst[i][j]) bfst[j][i]=bfst2[i][j]=true;

		//FORE(k,i,j-1) if(win[j][k]&&canlst[i][k]&&canfinal[k][j]) canlst[i][j]=true;
		if((bwin[j]&blst[i]&bfin2[j]).any()) canlst[i][j]=true;
		if(canlst[i][j]) blst[i][j]=blst2[j][i]=true;

		//printf("%d..%d: fst=%c lst=%c final=%c\n",i,j,canfst[i][j]?'v':'.',canlst[i][j]?'v':'.',canfinal[i][j]?'v':'.');
	}
	int ret=0; REP(i,n) if(canlst[0][i]&&canfst[i][n-1]) ++ret; return ret;
}

void run() {
	scanf("%d",&n);
	REP(i,n) win[i][i]=false;
	FOR(i,1,n) { string s; cin>>s; REP(j,i) win[i][j]=s[j]=='1',win[j][i]=!win[i][j]; }
	printf("%d\n",solve());
}

void stress() {
	REP(rep,100) {
		n=100;
		REP(i,n) win[i][i]=false;
		REP(i,n) REP(j,i) win[i][j]=rand()%2==0,win[j][i]=!win[i][j];
		solve();
	}
}

int main() {
	run();
	//stress();
	return 0;
}

