//long long int N, M, K, H, W, L, R;
long long int N, M, K, H, W, L, R;

class XorShift {
	unsigned int x, y, z, w, t;
public:
	XorShift() {
		x = 133553533;
		y = 314867339;
		z = 664298413;
		w = 999999937;
		t = 0;
	}
	unsigned int rand() {
		t = x ^ (x << 11);
		x = y;
		y = z;
		z = w;
		w = (w ^ (w >> 19)) ^ (t ^ (t >> 8));
		return w & 0x7fffffff;
	}
};

int main() {
	ios::sync_with_stdio(false);
	cin.tie(0);

	cin >> N;
	vector<vector<int>>v(N, vector<int>(N));
	for (int i = 1; i < N; i++) {
		string s;
		cin >> s;
		for (int j = 0; j < i; j++) {
			if (s[j] == '1')v[i][j] = 1;
			v[j][i] = v[i][j] ^ 1;
		}
	}
	set<int>cand;
	XorShift xs;
	for (int i = 0; i < 5000; i++) {
	//	cout << i << endl;
		vector<int>w(N);
		for (int j = 0; j < N; j++) {
			w[j] = j;
		}
		while (w.size() > 1) {
			int nx = xs.rand() % (w.size() - 1);
			if (v[w[nx]][w[nx + 1]])w.erase(w.begin() + nx + 1);
			else w.erase(w.begin() + nx);
		}
		cand.insert(w.front());
	}
	//for (auto i : cand)cout << i << endl;
	vector<int>w(N);
	queue<int>Q;
	for (auto i : cand) {
		w[i] = 1;
		Q.push(i);
	}
	while (!Q.empty()) {
		int cn = Q.front();
		Q.pop();
		for (int i = 0; i < cn; i++) {
			if (w[i])continue;
			if (v[i][cn]) {
				bool flag = false;
				if (!i)flag = true;
				for (int j = 0; j < i; j++) {
					if (v[i][j] && w[j])flag = true;
				}
				if (flag) {
					w[i] = 1;
					Q.push(i);
				}
			}
		}
		for (int i = cn + 1; i < N; i++) {
			if (w[i])continue;
			if (v[i][cn]) {
				bool flag = false;
				if (i==N-1)flag = true;
				for (int j = i+1; j < N; j++) {
					if (v[i][j] && w[j])flag = true;
				}
				if (flag) {
					w[i] = 1;
					Q.push(i);
				}
			}
		}
	}
	cout << accumulate(w.begin(), w.end(),0) << endl;
}

