char buf[max_n], a[max_n][max_n], dp[max_n][max_n][3];

int n;

int main() {
    //freopen("input.txt", "r", stdin);
    //freopen("output.txt", "w", stdout);
    scanf("%d", &n);
    for (int i = 1; i < n; ++i) {
        scanf("%s", buf);
        for (int j = 0; j < i; ++j) {
            a[i][j] = buf[j] - '0';
            a[j][i] = 1 ^ a[i][j];
        }
    }
    for (int i = 0; i < n; ++i) {
        dp[i][i][0] = 1;
        dp[i][i][1] = 1;
        dp[i][i][2] = 1;
    }
    for (int len = 2; len <= n; ++len) {
        for (int l = 0; l + len <= n; ++l) {
            int r = l + len - 1;
            if (len == 2) {
                dp[l][r][2] = 1;
            } else {
                for (int i = l + 1; i < r && !dp[l][r][2]; ++i) {
                    dp[l][r][2] |= dp[l][i][2] & dp[i][r][2];
                }
            }
            if (a[l][r] && dp[l][r][2]) {
                dp[l][r][0] = 1;
            }
            for (int i = l + 1; i < r && !dp[l][r][0]; ++i) {
                if (a[l][i] && dp[l][i][0] && dp[i][r][0]) {
                    dp[l][r][0] = 1;
                }
            }
            if (a[r][l] && dp[l][r][2]) {
                dp[l][r][1] = 1;
            }
            for (int i = l + 1; i < r && !dp[l][r][1]; ++i) {
                if (a[r][i] && dp[l][i][1] && dp[i][r][1]) {
                    dp[l][r][1] = 1;
                }
            }
        }
    }
    int ans = 0;
    for (int i = 0; i < n; ++i) {
        bool ok = dp[0][i][1] & dp[i][n - 1][0];
        ans += ok;
    }
    printf("%d\n", ans);
    return 0;
}

