#include <bits/stdc++.h>
using namespace std;
typedef long long int ll;
typedef pair<int, int> pii;
typedef pair<ll, int> pli;
typedef pair<ll, ll> pll;

const int MAX_N = 2000;

int N;
bool A[MAX_N][MAX_N];

bool dpl[MAX_N][MAX_N], dpr[MAX_N][MAX_N];

ll solve() {
    for (int i = 0; i < N; i++) {
        dpl[i][i] = dpr[i][i] = true;
    }
    for (int l = 1; l < N; l++) {
        for (int i = 0; i + l < N; i++) {
            int j = i + l;
            for (int k = i + 1; k <= j; k++) {
                dpl[i][j] =
                    dpl[i][j] || (A[i][k] && dpl[k][j] && dpr[i + 1][k]);
            }
        }
        for (int j = l; j < N; j++) {
            int i = j - l;
            for (int k = i; k < j; k++) {
                dpr[i][j] =
                    dpr[i][j] || (A[j][k] && dpr[i][k] && dpl[k][j - 1]);
            }
        }
    }

    int ans = 0;
    for (int i = 0; i < N; i++) {
        if (dpr[0][i] && dpl[i][N - 1]) {
            ans++;
        }
    }
    return ans;
}

int main() {
    cin >> N;
    for (int i = 1; i < N; i++) {
        string s;
        cin >> s;
        for (int j = 0; j < i; j++) {
            A[i][j] = s[j] == '1';
            A[j][i] = !A[i][j];
        }
    }

    cout << solve() << endl;

    return 0;
}

