bool beg(int bas,int son) {

	if(bas==son) return 1;

	if(bas>son) return 0;

	if(vb[bas][son]) return rb[bas][son];

	vb[bas][son]=1;

	for(int i:v[bas]) {

		if(en(bas+1,i) && beg(i,son)) {

			rb[bas][son]=1;

			break ;

		}

	}

	return rb[bas][son];

}

