constexpr int Nmax = 2002;
int main()
{
    int N;
    cin >> N;
    vector<bitset<Nmax>> A(N + 2, bitset<Nmax>());
    FOR(i, 1, N)
    {
        string S;
        cin >> S;
        REP(j, i)
        {
            A[i + 1][j + 1] = S[j] - '0';
            A[j + 1][i + 1] = !A[i + 1][j + 1];
        }
    }

    vector<bitset<Nmax>> dp(N + 2, bitset<Nmax>()); // (l, r)
    REP(i, N + 1) dp[i][i + 1] = 1;

    FOR(d, 2, N + 1) REP(l, N + 2 - d)
    {
        FOR(c, l + 1, l + d) if (dp[l][c] and dp[c][l + d] and (A[l][c] or A[l + d][c]))
        {
            dp[l][l + d] = 1;
            break;
        }
    }

    int ret = 0;
    // dbg(dp);
    FOR(i, 1, N + 1) ret += (dp[0][i] and dp[i][N + 1]);
    cout << ret << endl;
}

