#include <bits/stdc++.h>
using namespace std;
#define repex(i, a, b, c) for(int i = a; i < b; i += c)
#define repx(i, a, b) repex(i, a, b, 1)
#define rep(i, n) repx(i, 0, n)
#define repr(i, a, b) for(int i = a; i >= b; i--)

int main(){

    // 1. .
    int N;
    scanf("%d", &N);
    vector<bitset<2020>> v(N);
    repx(i, 1, N){
        char c[2020];
        scanf("%s", c);
        rep(j, i){
            if(c[j] == '1') v[i][j] = 1;
            else            v[j][i] = 1;
        }
    }

    // 2. dp.
    vector<bitset<2020>> l(N), r(N);
    rep(i, N) l[i][i] = r[i][i] = 1;
    repx(i, 1, N){
        rep(j, N - i){
            l[i + j][j] = (l[i + j] & r[j + 1]     & v[j]).any();
            r[j][i + j] = (r[j]     & l[i + j - 1] & v[i + j]).any();
        }
    }

    // 3. .
    printf("%d\n", (r[0] & l[N - 1]).count());
    return 0;
}

