int n;

bitset<2020> L[2020],R[2020],C[2020];
char def[2020][2020];

int main() {
	read(n);
	for(int i=2;i<=n;i++){
		cin>>(def[i]+1);
		for(int j=1;j<i;j++){
			if(def[i][j] == '0'){
				C[j][i] = 1;
			}else{
