		{
			int p = N * i + i + l;
			for (int k = i + 1, q = N * i + k, r = N * (i + l) + k, s = N * k + i + l; k < i + l; k++, q++, r++, s += N)
				if (dp[p] |= dp[q] & (m[q] | m[r]) & dp[s]) break;
		}
	deque<bool> dpL(N, false);
	dpL[N - 1] = true;
	for (int i = N - 2; i >= 0; i--)
		for (int j = i + 1; j < N; j++) dpL[i] |= m[N * i + j] & dpL[j] & dp[N * i + j];
