        scanf("%s", s);
        for (int j = 0; j < i; j++) {
            if (s[j] == '1') {
                g[i][j] = 1;
                g[j][i] = 0;
            } else {
                g[i][j] = 0;
                g[j][i] = 1;
            }
        }
    }
    for (int i = 0; i < n; i++) dpl[i][i] = dpr[i][i] = true;
    for (int len = 1; len < n; len++) {
        for (int i = 0; i + len < n; i++) {
            int j = i + len;
            // dpl[i][j] iff g[i][k] && exist k let dpl[k][j] && dpr[k][i+1]
            dpl[j][i] = (g[i] & dpl[j] & dpr[i + 1]).any();

            // dpr[j][i] iff g[j][k] && dpl[k][j-1] && dpr[k][i]
            dpr[i][j] = (g[j] & dpl[j - 1] & dpr[i]).any();
        }
    }
    int ans = 0;
    for (int i = 0; i < n; i++) {
        if (dpl[n - 1][i] && dpr[0][i]) ans++;
    }
