    string s; cin >> s;
    for (int j = 0; j < i; ++j) {
      a[i][j] = s[j] == '1';
      a[j][i] = s[j] == '0';
    }
  }
  V< bitset<2000> > dp(n);
  for (int i = 0; i < n; ++i) {
    dp[i][i] = true;
    if (i) dp[i - 1][i] = a[i][i - 1];
    if (i + 1 < n) dp[i + 1][i] = a[i][i + 1];
  }
  for (int w = 2; w < n; ++w) {
    for (int i = 0; i < n; ++i) {
