      if (j < n) {
        dp[j][i] = (a[i] & dp[i + 1] & dp[j])._Find_next(i) <= j;
      }
      j = i - w;
      if (j >= 0) {
        dp[j][i] = (a[i] & dp[i - 1] & dp[j])._Find_next(j - 1) <= i - 1;
      }
    }
  }
  int res = 0;
  for (int i = 0; i < n; ++i) res += dp[0][i] and dp[n - 1][i];
  cout << res << '\n';
