        }
    }
    int Res = 0;
    for (int n = 0; n < N; ++n) {
        if (DP[n][0] && DP[n][N-1]) ++Res;
    }
    cout << Res << endl;

    return 0;
}

#include "bits/stdc++.h"
using namespace std;
bitset <2005> pre[2005], suf[2005];
bitset <2005> preA[2005], sufA[2005];

int dp[2005][2005];
int a[2005][2005];
int n;
char txt[2005];

int delP[2005], delS[2005];

int main(int argc, char const *argv[])
{
	scanf("%d", &n);
	for(int i = 1; i <= n; i++) {
		a[i][i] = 1;
	}
	for(int i = 2; i <= n; i++) {
		scanf("%s", txt);
		for(int j = 1; j < i; j++) {
			int c = txt[j - 1] - '0';
			a[i][j] = c;
			a[j][i] = c ^ 1;
		}
	}
	for(int len = 1; len < n; len++) {
		for(int i = 1; i <= n; i++) {
			int j = i + len;
			if(j > n) break;
			if(len == 1) {
				dp[i][j] = 1;
			} else {
				bitset <2005> xx = preA[i] & suf[j];
				xx |= pre[i] & sufA[j];
				if(xx.count() > 0) {
					dp[i][j] = 1;
				} else {
					dp[i][j] = 0;
				}
			}
			if(dp[i][j]) {
				preA[i][j] = 1;
				sufA[j][i] = 1;
				if(a[j][i]) {
					suf[j][i] = 1;
				}
				if(a[i][j]) {
					pre[i][j] = 1;
				}
			}
		}
	}
	delP[1] = 1;
	for(int i = 2; i <= n; i++) {
		for(int j = 1; j < i; j++) {
			if(a[i][j] && dp[j][i]) {
				delP[i] |= delP[j];
			}
		}
	}
	delS[n] = 1;
	for(int i = n - 1; i >= 1; i--) {
		for(int j = i + 1; j <= n; j++) {
			if(a[i][j] && dp[i][j]) {
				delS[i] |= delS[j];
			} 
		}
	}
	int cnt = 0;
	for(int i = 1; i <= n; i++) {
		if(delP[i] && delS[i]) {
			++cnt;
		}
	}
	printf("%d\n", cnt);
	return 0;
}

