const ll SZ = 2010;

ll n;
bool grid[SZ][SZ];
int sv[SZ][SZ];
vector<ll> wn[SZ];

bool find(int l, int r) {
	if (l == r - 1) return 1;
	if (sv[l][r] != -1) return sv[l][r];


	int ind = lower_bound(wn[r].begin(), wn[r].end(), l) - wn[r].begin();
	while (ind < wn[r].size() && wn[r][ind] < r) {
		int i = wn[r][ind];
		bool fl1 = find(l, i);
		if (fl1) {
			if (find(i, r)) {
				sv[l][r] = 1;
				return 1;
			}
		}
		ind++;
	}

	ind = upper_bound(wn[l].begin(), wn[l].end(), r) - wn[l].begin();
	ind--;
	while (ind >= 0 && wn[l][ind] < r) {
		int i = wn[l][ind];
		bool fl1 = find(l, i);
		if (fl1) {
			if (find(i, r)) {
				sv[l][r] = 1;
				return 1;
			}
		}
		ind--;
	}


	sv[l][r] = 0;
	return 0;
}

