		dpl[i][i] = dpr[i][i] = 1;
	for(int len = 2; len <= n; len++)
	{
		for(int i = len; i <= n; i++)
		{
			if((dpr[i - 1] & dpl[i - len + 1] & wins[i]).any())
				dpl[i - len + 1][i] = 1;
		}
		for(int i = n - len + 1; i >= 1; i--)
		{
			if((dpl[i + 1] & dpr[i + len - 1] & wins[i]).any())
				dpr[i + len - 1][i] = 1;
		}
	}
	int ans = 0;
	for(int i = 1; i <= n; i++)
		if(dpl[1][i] && dpr[n][i])
			++ans;
	cout << ans << "\n";
	return 0;
}

