class edgeClass {// 
public:
	edgeClass() {
		x = 0, y = 0, weight = 1;
	}
	void swap() {
		int c = x;
		x = y;
		y = c;
	}
	int x, y, weight;
};
bool edgeClass_compare(const edgeClass& a, const edgeClass& b) {// key
	int x = b.x - a.x;
	if (x > 0) { return true; }
	x = b.y - a.y;
	return x > 0;
}
class graphClass {// 
public:
	graphClass() {
		N = 0;
		M = 0;
		next = NULL;
		edge.clear();
		weight = NULL;
		directedFlag = false;
	}

	void activate(int n, bool directed = false) {// n
		directedFlag = directed;
		N = n;
		M = 0;
		edge.clear();
		if (next != NULL) { delete[] next; }
		if (weight != NULL) { delete[] weight; }
		next = new vector<edgeClass>[N];
		weight = new int[N];
		for (int i = 0; i < N; i++) { weight[i] = 1; }
	}
	void setNodeWeight(int n, int weight) {// nweight
		this->weight[n] = weight;
	}
	void addEdge(int x, int y, int weight = 1) {// 
		edgeClass e;
		e.x = x;
		e.y = y;
		e.weight = weight;

		if (e.x < 0 || e.x >= N) { return; }

		edge.push_back(e); M++;
		next[e.x].push_back(e);

		if (directedFlag == false) {
			e.swap();
			next[e.x].push_back(e);
		}

	}
	void sortEdge() {// 
		for (int i = 0; i < N; i++) {
			if (next[i].size() > 1) {
				sort(next[i].begin(), next[i].end(), edgeClass_compare);
			}
		}
	}

	bool isTree() {// 
		if (N <= 0 || M != N - 1) { return false; }

		bool* visited = new bool[N];
		for (int i = 0; i < N; i++) {
			visited[i] = (i == 0);
		}

		queue<int> q; q.push(0);

		while (q.size() > 0) {
			int n = q.front(); q.pop();
			visited[n] = true;

			for (auto itr = next[n].begin(); itr != next[n].end(); itr++) {
				if (visited[itr->y] == false) {
					q.push(itr->y);
				}
			}
		}

		for (int i = 0; i < N; i++) {
			if (visited[i] == false) { delete[] visited; return false; }
		}

		delete[] visited;
		return true;
	}

	long* getDistanceFrom(int n) {// n
		if (n < 0 || n >= N) { return NULL; }

		long* buf = new long[N];
		for (int i = 0; i < N; i++) {
			buf[i] = 1000000000;
		}

		// 
		// pair<, >
		priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int>>> q;

		buf[n] = 0;
		q.push(pair<int, int>(0, n));

		while (q.empty() == false) {
			pair<int, int> p = q.top(); q.pop();
			int k = p.second;
			if (p.first <= buf[k]) {
				for (auto itr = next[k].begin(); itr != next[k].end(); itr++) {
					if (buf[itr->y] > buf[k] + itr->weight) {
						buf[itr->y] = buf[k] + itr->weight;
						q.push(pair<int, int>(buf[itr->y], itr->y));
					}
				}
			}
		}

		return buf;
	}

	void debugCout() {// 
		cout << endl;
		cout << " = " << N << "" << endl;
		cout << " = " << M << "" << endl;
		cout << endl;

		for (int i = 0; i < N; i++) {
			cout << "i = " << i << " (weight = " << weight[i] << "),  = ";
			if (next[i].size() == 0) { cout << "" << endl; continue; }
			auto itr = next[i].begin();
			cout << itr->y << "(weight = " << itr->weight << ")";
			itr++;

			for (; itr != next[i].end(); itr++) {
				cout << ", " << itr->y << "(weight = " << itr->weight << ")";
			}
			cout << endl;
		}
		cout << endl;
	}

	bool directedFlag;// 
	int N, M;
	int* weight;// 
	vector<edgeClass>* next;// 
	vector<edgeClass> edge;// 
};



void bfs(graphClass& G, int root) {// 
	int N = G.N;
	bool* visited = new bool[N];
	for (int i = 0; i < N; i++) {
		visited[i] = (i == root);
	}

	queue<int> q; q.push(root);

	while (q.size() > 0) {
		int n = q.front(); q.pop();
		visited[n] = true;

		// 

		for (auto itr = G.next[n].begin(); itr != G.next[n].end(); itr++) {

			// 

			if (visited[itr->y] == false) {// 
				q.push(itr->y);
			}
		}
	}

	delete[] visited;
}
int dfs_count;
bool* dfs_visited = NULL;
void dfs_process_main(graphClass & G, int n, int k) {// 
	dfs_visited[n] = true;

	// 
	dfs_count++;

	for (auto itr = G.next[n].begin(); itr != G.next[n].end(); itr++) {

		if (n < k) {
			if (itr->y < k && dfs_visited[itr->y] == false) {// 
				dfs_process_main(G, itr->y, k);
			}
		}
		else if (n > k) {
			if (itr->y > k && dfs_visited[itr->y] == false) {// 
				dfs_process_main(G, itr->y, k);
			}
		}
		else {
			if (dfs_visited[itr->y] == false) {// 
				dfs_process_main(G, itr->y, k);
			}
		}
	}
}
bool dfs(graphClass & G, int root) {// 
	dfs_count = 0;

	int N = G.N;
	dfs_visited = new bool[N];
	for (int i = 0; i < N; i++) { dfs_visited[i] = false; }
	dfs_process_main(G, root, root);

	delete[] dfs_visited;
	dfs_visited = NULL;

	if (dfs_count == G.N) {
		return true;
	}
	return false;
}

