int main()
{
  int N;
  cin >> N;
  for (auto i = 1; i < N; i++)
  {
    string S;
    cin >> S;
    for (auto j = 0; j < i; j++)
    {
      if (S[j] == '1')
      {
        W[i][j] = true;
        W[j][i] = false;
      }
      else
      {
        W[i][j] = false;
        W[j][i] = true;
      }
    }
  }
  for (auto i = 0; i < N; i++)
  {
    dp_L[i][i] = true;
    dp_R[i][i] = true;
  }
  for (auto n = 1; n < N; n++)
  {
    for (auto a = 0; a + n < N; a++)
    {
      int b = a + n;
      dp_L[a][b] = false;
      for (auto c = a + 1; c < b; c++)
      {
        if (dp_L[a][c] && dp_L[c][b])
        {
          dp_L[a][b] = true;
          break;
        }
      }
      if (!dp_L[a][b] && W[a][b])
      {
        for (auto c = a; c < b; c++)
        {
          if (dp_L[a][c] && dp_R[c + 1][b])
          {
            dp_L[a][b] = true;
            break;
          }
        }
      }
      dp_R[a][b] = false;
      for (auto c = a + 1; c < b; c++)
      {
        if (dp_R[a][c] && dp_R[c][b])
        {
          dp_R[a][b] = true;
          break;
        }
      }
      if (!dp_R[a][b] && W[b][a])
      {
        for (auto c = a; c < b; c++)
        {
          if (dp_L[a][c] && dp_R[c + 1][b])
          {
            dp_R[a][b] = true;
            break;
          }
        }
      }
#if DEBUG == 1
      cerr << "dp_L[" << a << "][" << b << "] = " << dp_L[a][b] << endl;
      cerr << "dp_R[" << a << "][" << b << "] = " << dp_R[a][b] << endl;
#endif
    }
  }
  int ans = 0;
  for (auto i = 0; i < N; i++)
  {
    if (dp_R[0][i] && dp_L[i][N - 1])
    {
      ans++;
    }
  }
  cout << ans << endl;
}

