void solve() {
	int i,j,k,l,r,x,y; string s;

	cin>>N;
	for(j=1;j<N;j++) {
		cin>>s;
		FOR(i,j) {
			A[j][i]=s[i]=='1';
			A[i][j]=s[i]=='0';
		}
	}

	FOR(x,N) win[x][x][0]=win[x][x][1]=1;
	for(l=2;l<=N;l++) {
		for(x=0;x+l<=N;x++) {
			y=x+l-1;
			for(i=x;i<y && dp[x][y]==0;i++) dp[x][y] |= win[x][i][0]&win[i+1][y][1];
			for(i=x+1;i<=y && win[x][y][0]==0;i++) win[x][y][0] |= A[x][i] & dp[x][i] & win[i][y][0];
			for(i=x;i<=y-1 && win[x][y][1]==0;i++) win[x][y][1] |= A[y][i] & dp[i][y] & win[x][i][1];
		}
	}
	int ret=0;
	FOR(i,N) {
		ret+=win[0][i][1]&win[i][N-1][0];
	}

	cout<<ret<<endl;

}


