void solve() {
	int n; cin >> n;
	rep1(i, n - 1) {
		rep(j, i) {
			char t; cin >> t;
			if (t == '1') {
				G[i].push_back(j);
			}
			else {
				G[j].push_back(i);
			}
		}
	}
	int ans = 0;
	rep(i, n) {
		//left
		if (i > 0) {
			int cnt = 0;
			fill(b, b + n, false);
			queue<int> q; q.push(i);
			while (!q.empty()) {
				int id = q.front(); q.pop();
				rep(j, G[id].size()) {
					int to = G[id][j];
					if (to >= i)break;
					if (b[to])continue;
					b[to] = true;
					cnt++;
					q.push(to);
				}
			}
			//cout << cnt << endl;
			if (cnt != i)continue;
		}
		//right
		if (i < n - 1) {
			int cnt = 0;
			fill(b, b + n, false);
			queue<int> q; q.push(i);
			while (!q.empty()) {
				int id = q.front(); q.pop();
				per(j, (int)G[id].size()) {
					int to = G[id][j];
					if (to <= i)break;
					if (b[to])continue;
					b[to] = true;
					cnt++;
					q.push(to);
				}
			}
			//cout << cnt << endl;
			if (cnt != n-1-i)continue;
		}
		ans++;
	}
	cout << ans << endl;
}

