#include <bits/stdc++.h>
using namespace std;

int main(){
    int N;
    const int M = 2000;
    bitset<M> A[M];
    cin >> N;
    for(int i=1; i<N; i++){
        string s;
        cin >> s;
        for(int j=0; j<i; j++){
            A[i][j] = s[j] - '0';
            A[j][i] = 1 - A[i][j];
        }
    }

    bitset<M> dpl[M], dpr[M];
    for(int i=0; i<N; i++) dpl[i][i] = dpr[i][i] = 1;

    for(int d=1; d<N; d++){
        bitset<M> resl, resr;
        bitset<M> maskl, maskr;
        for(int k=0; k<N; k++){
            if(k-1 >= 0) maskl[k-1] = 1;
            if(k+d < N) maskr[k+d] = 1;

            bitset<M> l = ~A[k];
            l &= maskl;
            l &= dpr[k] >> 1;
            l &= dpl[k] >> d;
            resl |= l;

            bitset<M> r = ~A[k];
            r &= maskr;
            r &= dpl[k] << 1;
            r &= dpr[k] << d;
            resr |= r;

            if(k-d >= 0) maskl[k-d] = 0;
            if(k+1 < N) maskr[k+1] = 0;
        }
        for(int i=0; i<N; i++){
            if(i+d < N) dpl[i][i+d] = resl[i];
            if(i-d >= 0) dpr[i][i-d] = resr[i];
        }
    }

    int ans = 0;
    for(int i=0; i<N; i++) if(dpl[i][N-1] && dpr[i][0]) ans++;
    cout << ans << endl;
    return 0;
}

