int main() {
    // source
    int n;
    cin >> n;
    string s;
    vector<vector<int>> vec(n + 1);
    repd(i, 2, n + 1) {
        cin >> s;
        rep(l0, s.size()) {
            if (s[l0] == '1') {
                vec[i].push_back(l0 + 1);
            } else {
                vec[l0 + 1].push_back(i);
            }
        }
    }

    // judge

    int judge[n + 1] = {};
    int count = 0;
    int ans = 0;

    repd(i, 1, n + 1) {
        rep(l0, n + 1) { judge[l0] = 0; }
        queue<int> q;
        count = 0;

        judge[i] = 1;
        count++;
        rep(l0, vec[i].size()) { q.push(vec[i][l0]); }
        while (!q.empty()) {
            int f = q.front();
            rep(l0, vec[f].size()) {
                if (judge[vec[f][l0]] == 0) {
                    q.push(vec[f][l0]);
                    count++;
                }
            }
        }
        if (count == n) {
            ans++;
        }
    }

    cout << ans << endl;
    return 0;
}

