const int MAXN=2000;

int n;
bool win[MAXN][MAXN];

bool canfst[MAXN][MAXN];
bool canlst[MAXN][MAXN];
bool canfinal[MAXN][MAXN];

int solve() {
	//REP(i,n) { printf("win%d: ",i); REP(j,n) printf("%c",win[i][j]?'v':'.'); puts(""); }
	REP(i,n) REP(j,n) canfst[i][j]=canlst[i][j]=canfinal[i][j]=false;
	REP(i,n) canfst[i][i]=canlst[i][i]=true;
	FORE(len,2,n) REPE(i,n-len) {
		int j=i+len-1;
		FORE(k,i,j-1) if(canfst[i][k]&&canlst[k+1][j]) canfinal[i][j]=true;
		FORE(k,i+1,j) if(win[i][k]&&canfst[k][j]&&canfinal[i][k]) canfst[i][j]=true;
		FORE(k,i,j-1) if(win[j][k]&&canlst[i][k]&&canfinal[k][j]) canlst[i][j]=true;
		//printf("%d..%d: fst=%c lst=%c final=%c\n",i,j,canfst[i][j]?'v':'.',canlst[i][j]?'v':'.',canfinal[i][j]?'v':'.');
	}
	int ret=0; REP(i,n) if(canlst[0][i]&&canfst[i][n-1]) ++ret; return ret;
}

void run() {
	scanf("%d",&n);
	REP(i,n) win[i][i]=false;
	FOR(i,1,n) { string s; cin>>s; REP(j,i) win[i][j]=s[j]=='1',win[j][i]=!win[i][j]; }
	printf("%d\n",solve());
}

int main() {
	run();
	return 0;
}

