void dfs(int node, int par){
	u[node]++;
	say += u[node] == 1;
	for(int i = 0; i < g[node].size(); i++)
		if(!u[coc]){
			if(par < node and coc < par)
				continue;
			if(par > node and coc > par)
				continue;
			dfs(coc, node);
		}
}

