const int MAX = 2e3 + 41;
//const int MAX = 10;

int n;
bitset<MAX> pref[MAX], suf[MAX], win[MAX], can[MAX];
int a[MAX][MAX];

void solve () {
	fi(0, n - 1) {
		fj(0, n - 1) {
			if (a[i][j]) win[i].set(j);
		}
	}
//	fi(0, n - 1) dbg(win[i]);
//	dbg(win[0]);
	fi(0, n - 1) {
		pref[i].set(i);
		suf[i].set(i);
		can[i].set(i);
		fdj(i - 1, 0) {
			if (a[i][j]) {				
				if ( ( (suf[j] << 1) & pref[i]).count() ) {
					pref[i] |= pref[j];
				}
			}
		}
		fdj(i - 1, 0) {
			if ( (pref[i] & (suf[j] << 1)).count() ) {
				can[j].set(i);
			}
		}
		bitset<MAX> sufwin;
		sufwin.reset();
		sufwin.set(i);
		fdj(i - 1, 0) {
			if ( (sufwin & win[j] & can[j]).count() ) {
				sufwin.set(j);
				suf[j].set(i);
			}
		}
/*
		fj(0, i) {
			dbg(pref[j]);
			dbg(suf[j]);
		}
		err("after i = %d\n", i);
*/
	}
	vi answer;
	fi(0, n - 1) {
		if (pref[i].test(0) && suf[i].test(n - 1)) {
			answer.pb(i);
		}
	}
	printf("%d\n", sz(answer));
//	dbg(answer);	
}

