using namespace std;
constexpr unsigned long maxN = 2000;

void tape(long r, long l, vector<vector<bitset<maxN>>>& memo, vector<bitset<maxN>>& tapi){
    if(l < r || memo[r][l - r].any())return;
    for(unsigned long i = r; i <= l; ++i){
        tape(r, i - 1, memo, tapi), tape(i, l, memo, tapi);
        memo[r][l - r][i] = ((memo[r][i - 1 - r] | memo[i][l - i]) & tapi[i]).any();
    }
}

