        for(int j = i - 1; j >= 1; j--) {
            after[i][j] = after[i-1][j];
            if(after[minimum][j] >= minimum) {
                chmax(after[i][j], i);
            } else if(after[i][j] + 1 >= before[i][i]) {
                if(A[j][i] == 1) chmax(after[i][j], i);
            }
