int n;
bitset<2000> a[2000];
int nx[2000][2001];
bitset<2000> e[2001000], f[2001000];
bitset<2000> *d[2000][2000], *vis[2000][2000];
char s[2222];
bitset<2000> pref[2001];
int win[2000][2000];

int solve(int from, int to, int ind) {
    if (win[from][to] != -1) {
        if (win[from][to] == ind) {
            d[from][to]->set(ind - from);
            return 1;
        }
        return 0;
    }
    vis[from][to]->set(ind - from);
    if (ind > from) {
        bool ok = false;
        if (((a[ind] >> from) & (*d[from][ind - 1])).any()) {
            ok = true;
        }
        if (!ok) {
            for (int i = nx[ind][from]; i < ind; i = nx[ind][i + 1]) {
                if (!vis[from][ind - 1]->test(i - from) && solve(from, ind - 1, i)) {
                    ok = true;
                    break;
                }
            }
        }
        if (!ok) {
            return 0;
        }
    }
    if (ind < to) {
        bool ok = false;
        if (((a[ind] >> (ind + 1)) & (*d[ind + 1][to])).any()) {
            ok = true;
        }
        if (!ok) {
            for (int i = nx[ind][ind + 1]; i <= to; i = nx[ind][i + 1]) {
                if (!vis[ind + 1][to]->test(i - ind - 1) && solve(ind + 1, to, i)) {
                    ok = true;
                    break;
                }
            }
        }
        if (!ok) {
            return 0;
        }
    }
    d[from][to]->set(ind - from);
    return 1;
}

