                f1 |= dp[0][j][k] and dp[1][i+1][k] and win[i][k];
            }
            dp[0][j][i] = f1, dp[1][i][j] = f2;
        }
    }

    int ans = 0;
    for(int i=0; i<N; i++) ans += (dp[0][N-1][i] and dp[1][0][i]);
    printf("%d\n", ans);
    return 0;
}

