{
	fastInp;

	cin >> n;

	for (int i = 1; i <= n + 1; i++) {
		sv[i - 1][i] = 1;
		sv2[i][i - 1] = 1;
	}
	for (int i = 1; i < n; i++) {
		string s;
		cin >> s;
		for (int j = 0; j < s.size(); j++) {
			if (s[j] == '1') {
