			}
			else {
				grid[j][i] = 1;
			}
		}
	}

	for (int i = 2; i <= n + 1; i++) {
		for (int j = 0; j <= n; j++) {
			ll lft = j, rt = j + i;
			if (rt <= n + 1) {
				if ((sv[lft] & grid[lft]) != 0 || (sv2[rt] & grid[rt]) != 0) {
					sv[lft][rt] = 1;
					sv2[rt][lft] = 1;
				}
			}
		}
	}

	ll ans = 0;
	for (int i = 1; i <= n; i++) {
		if (sv[0][i] && sv[i][n + 1]) {
			ans++;
		}
	}

	cout << ans;

	return 0;
}

