int N;
int A[2020][2020];

int win[2020][2020][2];
int dp[2020][2020];

int hoge(int L,int R,int side) ;
int top(int L,int R) {
	if(dp[L][R]>=0) return dp[L][R]>=0;
	if(L==R) return dp[L][R]=1;
	int x;
	for(x=L;x<R;x++) if(hoge(L,x,0)&&hoge(x+1,R,1)) return dp[L][R]=1;
	return dp[L][R]=0;
}

int hoge(int L,int R,int side) {
	if(L==R) return 1;
	if(win[L][R][side]>=0) return win[L][R][side];
	win[L][R][side]=0;
	if(side==0) {
		for(int x=L+1;x<=R;x++) if(A[L][x]&&top(L,x)&&hoge(x,R,0)) return win[L][R][side]=1;
	}
	else {
		for(int x=L;x<R;x++) if(A[R][x]&&top(x,R)&&hoge(L,x,1)) return win[L][R][side]=1;
	}
	return 0;

}

void solve() {
	int i,j,k,l,r,x,y; string s;

	cin>>N;
	for(j=1;j<N;j++) {
		cin>>s;
		FOR(i,j) {
			A[j][i]=s[i]=='1';
			A[i][j]=s[i]=='0';
		}
	}
	MINUS(win);
	MINUS(dp);
	int ret=0;
	FOR(i,N) if(hoge(0,i,1)&&hoge(i,N-1,0)) ret++;
	cout<<ret<<endl;

}


