template <size_t N> bool bitsetOpAny(const bitset<N> &a, const bitset<N> &b, const bitset<N> &c, const bitset<N> &d) {
  constexpr size_t nw = (N + 64 - 1) / 64;
  auto a_ = (const uint64_t *)&a;
  auto b_ = (const uint64_t *)&b;
  auto c_ = (const uint64_t *)&c;
  auto d_ = (const uint64_t *)&d;
  for (size_t i = 0; i < nw; ++i) {
    if ((a_[i] | b_[i]) & c_[i] & d_[i]) {
      return true;
    }
  }
  return false;
}


