    for(int j = 0; j < i; j++) {
      int x = (int) s[j] - '0';
      mat[i][j] = x;
      mat[j][i] = 1 - x;
    }
  }

  for(int i = N - 1; i >= 0; i--) {
    winL[i][i] = winR[i][i] = 1;
