      }
      winL[i][j] = winR[i][j] = 0;
      {  // winL
        for(int k = i + 1; k <= j; k++) {
          winL[j][i] += mat[i][k] & winL[j][k] & sumP[i][k];
          if(winL[j][i]) break;
        }
      }
      {  // winR
        for(int k = i; k < j; k++) {
          winR[j][i] += mat[j][k] & winR[k][i] & sumP[k][j];
          if(winR[j][i]) break;
        }
      }
    }
  }
  int ret = 0;
  for(int i = 0; i < N; i++) {
    bool ans = winR[i][0] * winL[N - 1][i];
    ret += ans > 0;
  }
  cout << ret << endl;
}

