bitset<mn> t[mn][mn], g[mn];

int n;
char s[mn + 5];

bitset<mn> dp(int l, int r) {
    bitset<mn> &ans = t[l][r];
    if (ans.any()) return ans;
    if (l == r) {
        ans[l] = true;
        return ans;
    }
    for (int j = l; j <= r; j++) {
        if (j == l) {
            ans[j] = (dp(l + 1, r) & g[j]).any();
        } else if (j == r) {
            ans[j] = (dp(l, r - 1) & g[j]).any();
        } else ans[j] = (dp(l, j - 1) & g[j]).any()&&(dp(j+1, r) & g[j]).any();
    }
    //printf("%d %d %d%d%d%d%d%d\n",l,r,(int)ans[0],(int)ans[1],(int)ans[2],(int)ans[3],(int)ans[4],(int)ans[5]);
    return ans;
}

