#include <bits/stdc++.h>
int main() {
  std::cin.tie(0); std::cin.sync_with_stdio(false);
  size_t n; std::cin >> n;
  auto a = std::vector<std::vector<int>>(n + 2, std::vector<int>(n + 2, false));
  auto insert = [&] (size_t i, size_t j, bool k) {
    a[i][j] = k;
    a[j][i] = !k;
  };
  for (size_t i = 1; i <= n; i++) {
    insert(i, 0, true);
    insert(i, n + 1, true);
  }
  for (size_t i = 2; i <= n; i++) {
    for (size_t j = 1; j < i; j++) {
      char x; std::cin >> x;
      insert(i, j, x == '1');
    }
  }
  auto dp = std::vector<std::vector<int>>(n + 2, std::vector<int>(n + 2, false));
  for (size_t i = 0; i <= n; i++) dp[i][i + 1] = true;
  auto renew = [&] (size_t d) {
    assert(2 <= d && d <= n + 1);
    for (size_t i = 0, j = d; j <= n + 1; i++, j++) {
      for (size_t k = i + 1; k < j; k++) {
        dp[i][j] |= dp[i][k] && dp[k][j] && (a[i][k] || a[j][k]);
      }
    }
  };
  for (size_t d = 2; d <= n + 1; d++) renew(d);
  int ret = 0;
  for (size_t i = 1; i <= n; i++) {
    if (dp[0][i] && dp[i][n + 1]) ret++;
  }
  std::cout << ret << std::endl;
  return 0;
}

