int lef[ran], rig[ran];

int need_upd[ran][ran];
int cnu[ran];

int q[ran*ran], l, r;
bool inq[ran];

int main() {
	scanf("%d", &n);
	for(int i=2;i<=n;i++) {
		scanf("%s", s);
		for(int j=1;j<i;j++) {
			int x = s[j-1]-'0';
			if (x) need_upd[j][cnu[j]++] = i;
			else need_upd[i][cnu[i]++] = j;
			f[i][j] = x; f[j][i] = 1-x;
		}
	}

	l=r=0;
	for(int i=1;i<=n;i++) {
		if(i>1&&f[i][i-1]) lef[i] = i-1;
		else lef[i] = i;
		if(i<n&&f[i][i+1]) rig[i] = i+1;
		else rig[i] = i;

		q[r++] = i;
		inq[i] = true;
	}

	int lim = ran*ran-11;
	while(l!=r) {
		int cur = q[l++]; // cur = 1 [1,2]; nxt = 3 [3,3];
		if(l==lim) l=0;
		for(int i=0;i<cnu[cur];i++) {
			int nxt = need_upd[cur][i];
			bool upd = false;
			if(nxt<cur && rig[nxt] >= lef[cur]-1 && rig[nxt] < rig[cur]) {
				rig[nxt] = rig[cur];
				upd = true;
			}
			if(cur<nxt && rig[cur] >= lef[nxt]-1 && lef[nxt] > lef[cur]) {
				lef[nxt] = lef[cur];
				upd = true;
			}
