			A[j][i] = 1 - (A[i][j] = input[j] - '0');
	}
	for (int i = 1;i <= N;++i)
		F[i][i][0] = F[i][i][1] = 1;
	for (int i = 1;i <= N - 1;++i)
		F[i][i + 1][A[i + 1][i]] = 1;
	for (int len = 3;len <= N;++len)
		for (int l = 1, r = len;r <= N;++l, ++r)
		{
			for (int x = l + 1;x <= r;++x)
				F[l][r][1] |= (F[l][x][1] & F[x][r][1]);
			if (A[r][l])
				for (int mid = l;mid <= r - 1;++mid)
					F[l][r][1] |= (F[l][mid][0] & F[mid + 1][r][1]);
			for (int x = l;x <= r - 1;++x)
				F[l][r][0] |= (F[x][r][0] & F[l][x][0]);
			if (A[l][r])
				for (int mid = l + 1;mid <= r;++mid)
					F[l][r][0] |= (F[mid][r][1] & F[l][mid - 1][0]);
		}
	int Ans(0);
	for (int x = 1;x <= N;++x)
		Ans += (F[1][x][1] & F[x][N][0]);
	printf("%d", Ans);
	return 0;
}

