bool exists(ull *a, int l, int r) {
  while (l < r) {
    if (l % 64 == 0 && l + 64 <= r) {
      if (a[l / 64]) return true;
      l += 64;
    } else {
      if (test(a, l++)) return true;
    }
  }
  return false;
}

